import { Component, OnInit } from "@angular/core";
import { Store } from "@ngrx/store";
import { Observable } from "rxjs";
import { nextSelector, prevSelector } from "../anime.selector";
import { loadAnime } from "./anime.actions";
import { Anime } from "./anime.state";

@Component({
  selector: "app-anime",
  templateUrl: "./anime.component.html",
})
export class AnimePageComponent implements OnInit {
  anime$: Observable<Anime[]> = this.store.select((state) => state.anime);
  prvs = 0;
  nxt = 0;
  title: string = "";
  readonly prev$: Observable<number | null> = this.store.select(prevSelector);
  readonly next$: Observable<number | null> = this.store.select(nextSelector);

  constructor(private store: Store<{ anime: Anime[] }>) {
    this.prev$.subscribe((data: any) => {
      this.prvs = data;
    });
    this.next$.subscribe((data: any) => {
      this.nxt = data;
    });
  }

  ngOnInit(): void {
    this.store.dispatch(loadAnime({ input: "", page: 0 }));
  }

  search(): void {
    this.store.dispatch(loadList({ select: this.fname, page: 0, total: 20 }));
  }

  prevPage() {
    this.store.dispatch(loadAnime({ input: this.title, page: this.prvs }));
  }

  nextPage() {
    this.store.dispatch(loadAnime({ input: this.title, page: this.nxt }));
  }
}
<form (submit)="search()">
  FName: <input type="text" [(ngModel)]="title" name="title" />
  <button>Search</button>
</form>

<button *ngIf="prev$ | async" type="button" (click)="prevPage()">
  Previous
</button>
<button *ngIf="next$ | async" type="button" (click)="nextPage()">Next</button>

<div *ngFor="let anime of anime$ | async">
  <h3>{{ anime.title }}</h3>
  <p>{{ anime.synopsis }}</p>
  <div>
    <span>No. of episodes: {{ anime.episodes }} </span><br />
    <span>{{ anime.rating }} </span>
  </div>
</div>
