import { Component, OnInit } from "@angular/core";
import { Store } from "@ngrx/store";
import { Observable } from "rxjs";
import { loadAnime } from "./anime.actions";
import { Anime } from "./anime.state";

@Component({
  selector: "app-anime",
  templateUrl: "./anime.component.html",
})
export class AnimePageComponent implements OnInit {
  anime$: Observable<Anime[]> = this.store.select((state) => state.anime);

  constructor(private store: Store<{ anime: Anime[] }>) {}

  ngOnInit(): void {
    this.store.dispatch({ type: "[Anime Page] Load Anime" });
  }

  prev() {
    this.store.dispatch(loadAnime());
  }

  next() {
    this.store.dispatch(loadAnime());
  }
}
<button type="button" (click)="prev()">Previous</button>
<button type="button" (click)="next()">Next</button>
<!-- <button *ngIf="next$ | async" type="button" (click)="next()">Next</button> -->

<div *ngFor="let anime of anime$ | async">
  <h3>{{ anime.title }}</h3>
  <p>{{ anime.synopsis }}</p>
  <div>
    <span>No. of episodes: {{ anime.episodes }} </span><br />
    <span>{{ anime.rating }} </span>
  </div>
</div>
